    /**
     * The minimum level required to complete this task.
     */
    @JvmField
    val levelReq: Int

    /**
     * The combat level requirement for this task.
     */
    val combatCheck: Int

    /**
     * The tips related to task (message).
     */
    val tip: Array<String>

    /**
     * The npc ids associated with this task.
     */
    val npcs: IntArray

    /**
     * Indicates whether the task involves NPCs of the undead type.
     */
    var undead: Boolean = false

    /**
     * Indicates whether the task involves NPCs of the dragon type.
     */
    var dragon: Boolean = false

    /**
     * The number of npcs assigned to be defeated for this task.
     */
    var amtHash: Int = 0

    /**
     * Quest requirements needed to access this task.
     * (Empty if no quest required).
     */
    var questReq: String = ""

    /**
     * Constructs a task with the given attributes.
     * @param combatCheck Required combat level for the task.
     * @param ids IDs of associated NPCs.
     * @param info Tips or hints for the task.
     * @param levelReq Minimum player level required.
     * @param undead Whether the task involves undead NPCs.
     * @param dragon Whether the task involves dragons.
     */
    constructor(combatCheck: Int, ids: IntArray, info: Array<String>, levelReq: Int, undead: Boolean, dragon: Boolean) {
        this.levelReq = levelReq
        this.npcs = ids
        this.tip = info
        this.undead = undead
        this.dragon = dragon
        this.combatCheck = combatCheck
    }

    /**
     * Constructs a task including an optional hash for additional metadata.
     * @param combatCheck Required combat level for the task.
     * @param ids IDs of associated NPCs.
     * @param info Tips or hints for the task.
     * @param levelReq Minimum player level required.
     * @param undead Whether the task involves undead NPCs.
     * @param dragon Whether the task involves dragons.
     * @param amtHash Optional metadata hash.
     */
    constructor(combatCheck: Int, ids: IntArray, info: Array<String>, levelReq: Int, undead: Boolean, dragon: Boolean, amtHash: Int) {
        this.levelReq = levelReq
        this.npcs = ids
        this.tip = info
        this.undead = undead
        this.dragon = dragon
        this.amtHash = amtHash
        this.combatCheck = combatCheck
    }

    /**
     * Constructs a task that requires a specific quest to be completed.
     * @param combatCheck Required combat level for the task.
     * @param ids IDs of associated NPCs.
     * @param info Tips or hints for the task.
     * @param levelReq Minimum player level required.
     * @param questReq Required quest for task access.
     */
    constructor(combatCheck: Int, ids: IntArray, info: Array<String>, levelReq: Int, questReq: String) {
        this.combatCheck = combatCheck
        this.npcs = ids
        this.tip = info
        this.levelReq = levelReq
        this.questReq = questReq
    }

    /**
     * Checks whether the player meets the quest requirements for this task.
     * @param player The player to check.
     * @return True if the player meets the quest requirements or if no quest is required.
     */
    fun hasQuestRequirements(player: Player?): Boolean {
        return questReq.isEmpty() || hasRequirement(player!!, questReq, false)
    }

    /**
     * Gets the name of the npc for the task.
     */
    val displayName: String
        get() = NPCDefinition.forId(npcs[0]).name

    companion object {
        /**
         * Maps NPC ids to their corresponding tasks.
         */
        val taskMap: HashMap<Int, Tasks> = HashMap()

        init {
            Arrays.stream(values()).forEach { entry ->
                Arrays.stream(entry.npcs).forEach { id ->
                    taskMap.putIfAbsent(id, entry)
                }
            }
        }

        /**
         * Gets the task for the given npc id.
         * @param id npc id.
         * @return The corresponding task, or null if none exists.
         */
        @JvmStatic
        fun forId(id: Int): Tasks? {
            return taskMap[id]
        }
    }
}
